import { GET_POSTS } from '../actions'
import axios from 'axios'

const posts = (state = {}, action) => {
    switch (action.type) {
        case GET_POSTS:
            axios.get("http://localhost:3001/posts", { headers: { 'Authorization': 'whatever-you-want' } })
            .then(response => {
                return state
            })


        default:
            return state
    }
}

export default posts
