import React, { Component } from 'react'
import { connect } from 'react-redux'
import { getPostAsync } from '../actions'
import Comments from './Comments'

class Post extends Component {
    state = {
        postId: this.props.match.params.id
    }

    componentDidMount() {
        this.props.dispatch(getPostAsync(this.state.postId))
    }

    render() {
        return (
            <div>
                <div className="container">
                    <div className="jumbotron">
                        <h2>{this.props.posts.title}</h2>
                        <hr></hr>
                        <p>{this.props.posts.body}</p>

                        <div className="text-right">
                            <hr></hr>
                                    <button className="btn btn-success mr-1"><i className="fas fa-thumbs-up"></i></button>
                                    <button className="btn btn-danger mr-1"><i className="fas fa-thumbs-down"></i></button>
                                    <button className="btn btn-primary mr-1">Comment</button>
                        </div>

                        <div className="jumbotron">

                        </div>
                    </div>
                    <Comments postId={this.state.postId} />
                </div>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return state
}

export default connect(mapStateToProps)(Post)
