export const GET_POSTS = 'GET_POSTS'

export const getPosts = () => (
    {
        type: GET_POSTS
    }
)

function getPostsAsync() {
    return dispatch => {
      setTimeout(() => {
        // Yay! Can invoke sync or async actions with `dispatch`
        dispatch(getPosts());
      }, 1000);
    };
  }
