export const GET_POSTS = 'GET_POSTS'

const getPosts = () => (
    {
        type: GET_POSTS
    }
)

export function getPostsAsync() {
    return dispatch => {
      setTimeout(() => {
        // Yay! Can invoke sync or async actions with `dispatch`
        console.log("contando")
        dispatch(getPosts());
      }, 1000);
    };
  }
